sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
			var sSalutation = "",
				sGender = current.assigned_to.gender.toString(),
				sLastName = current.assigned_to.last_name.toString();

			if (sGender == null || sGender == "Male") { 
				sSalutation = "Mr. "+sLastName;
			}
			else {
				sSalutation = "Mrs. "+sLastName;
			}
			// gs.info('GM sSalutation = '+sSalutation);
	template.print(sSalutation);
})(current, template, email, email_action, event);
